<?php namespace App\Http\Controllers;use App\Models\Subscribers;use Illuminate\Http\Request;class SubscribersController extends Controller{public function store(Request $request){$request->validate(['email'=>'required|unique:subscribers,email|max:255|regex:/[-0-9a-zA-Z.+_]+@[-0-9a-zA-Z.+_]+.[a-zA-Z]{2,4}/',],['email.required'=>'هذا الحقل مطلوب','email.unique'=>'البريد الإلكتروني تم استخدامه من قبل','email.max'=>'يجب ألا يتعدي الاسم 255 حرف','email.regex'=>'صيغة البريد الإلكتروني غير صحيحة',]);$email=$request->email;$insert=Subscribers::create(['email'=>$email,]);if($insert->save()){return response(['status'=>true,'message'=>'تم إضافة بريدك الإلكتروني بنجاح','form'=>'reset']);}}}